catalog_ui_policy.script_false:
function onCondition() {
	// チェック済の場合
	g_form.setMandatory('category', true);
	g_form.setMandatory('subcategory', true);
	g_form.setMandatory('short_description', true);
	g_form.setMandatory('description', true);

	g_form.setDisplay('category', true);
	g_form.setDisplay('subcategory', true);
	g_form.setDisplay('short_description', true);
	g_form.setDisplay('description', true);
	g_form.setDisplay('watch_list', true);
}

catalog_ui_policy.script_true:
function onCondition() {
	// 未チェックの場合	
	g_form.setMandatory('category', false);
	g_form.setMandatory('subcategory', false);
	g_form.setMandatory('short_description', false);
	g_form.setMandatory('description', false);

	g_form.setDisplay('category', false);
	g_form.setDisplay('subcategory', false);
	g_form.setDisplay('short_description', false);
	g_form.setDisplay('description', false);
	g_form.setDisplay('watch_list', false);	
}
